/** @jsx React.DOM */
var React = require('react/addons');

var Header = React.createClass({displayName: 'Header',
  render: function() {
    return React.DOM.div( {style:this.headerStyle, className:"header"}, this.props.children)
  },

  headerStyle: {
    height: '60px',
    lineHeight: '60px',
    fontSize: '18px',
    padding: '0 10px',
    backgroundColor: '#f0611f',
    color: '#FFFFFF',
    transition: 'height 0.2s',

    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'stretch'
  }
});

module.exports = Header;
