/** @jsx React.DOM */
var Switch = React.createClass({displayName: 'Switch',
  getInitialState: function() {
    return {
      checked: false
    };
  },

  render: function() {
    var checked = this.state.checked;

    var cx = React.addons.classSet;
    var toggleBar = cx({
      toggleBar: true,
      checked: checked
    });

    var toggleRadio = cx({
      toggleRadio: true,
      checked: checked
    });

    var onRadio = cx({
      onRadio: true,
      fill: checked
    });

    return (
      React.DOM.div( {className:"toggleContainer", onClick:this.handleClick}, 
        React.DOM.div( {className:toggleBar}),
        React.DOM.div( {className:toggleRadio}, 
          React.DOM.div( {className:"radioContainer"}, 
            React.DOM.div( {className:"offRadio"}),
            React.DOM.div( {className:onRadio})
          )
        )
      )
    );
  },

  handleClick: function() {
    this.setState({checked: !this.state.checked});
  }
});


