/** @jsx React.DOM */

var React = require('react/addons');

var Shadow = require('./Shadow');

var Dialog = React.createClass({displayName: 'Dialog',
  render: function() {
    return (
      React.DOM.div( {style:this.dialogStyle}, 
        Shadow(null ),
        React.DOM.div(null,  " I am a dialog! " )
      )
    );
  },

  dialogStyle: {
    position: 'relative',
    margin: '10px',
    padding: '10px'
  }
});

module.exports = Dialog;
